import React,{useState} from "react";
import './handlingEvents';

const HandlingEvents = ()=>{
    const purple = '#8e44ad';
    const [bg,setBg] = useState(purple);
    const [name,setName] = useState("click me");

    const bgChange = () => {
        let newBg = '#34405e';
        setBg(newBg);
        setName('ohh ðŸ˜ƒ');
    }

    return(
        <>
        <div className="handling-div" style={{backgroundColor:bg}}>
           
            <button onClick={bgChange} className="handling-button">{name}</button>

        </div>
        </>

    )

}

export default HandlingEvents;
