import React,{useState} from "react";
import './basicForm.css';

const BasicForm = ()=>{

    const [name, setName] = useState("");

    const inputSubmit = (e) => {
          setName(e.target.value);
    }

    return(
        <>
        <div className="basicform-div">
            <h1 className="basicform-h1">Hello {name}</h1>
            <input type="text" className="basicform-input" placeholder="Enter your name"/>
            <button onChange={inputSubmit} className="basicform-button">Click me</button>
        
        </div>
        </>

    )

}

export default BasicForm;
